import styled from "styled-components"

export const ItemContainer = styled.div`
    width:80%
    

    h3{
        font-size:32px;
        color:#fff;

    }

    p{
        font-size:16px;
        color:#FAFAFA60;
      

    }

    a.remover {
        color:#FFFF;
        margin-top:20px;
       
    }

    hr{
        color:#fafafa60;
        margin: 20px 0;

    }

`
