import styled from "styled-components";


export const ButtonContainer =  styled.button`


   backgrounf-color: #FAFAFa;
   border:1px solid #fafafa;
   border-radius:20px;
   height:62px;
   width:80%;
   margin:20xp;


   &: hover{
    background-color:#fafafa60;
    
   }
  

`
