import {Container} from './styles'
import Input from '../components/Input';
import gitlogo from '../assets/gitx.png'
import ItemRepo from '../components/itemRepo';
import Button from '../components/button';
import { useState } from 'react';
import {api} from "../services/api"

function App() {
  const [currentRepo,setCurrenet] = useState('');
  const [repos, setrepos] = useState([]);

  ate
  const pesquisar = async()=>{
    const {data} = api.get(`repos/${currentRepo}`)

    if(data.id){setrepos(prev=> [...prev,data]);
    }

  }
  return (



    <Container>
    <img src={gitlogo} width={72} height={72} alt='icone'/>
    <Input/>

    <Button/>
    {repos.map(repos=> <ItemRepo/>)}

    </Container>




  );
}

export default App;
